# Load necessary packageslibrary(tidyverse)library(lmtest)# Read the dataset from a CSV filedata <- read.csv("downloads/kc_house_data.csv")# Perform a simple linear regression analysislm_model <- lm(price ~ sqft_living, data = data)# Display summary of the regression analysissummary(lm_model)# Check for assumptions of linear regression# 1. Residuals vs Fitted plotplot(lm_model, which = 1)# 2. Normal Q-Q plotplot(lm_model, which = 2)# 3. Scale-Location plotplot(lm_model, which = 3)# 4. Residuals vs Leverage plotplot(lm_model, which = 5)# Perform correlation test between price and sqft_livingcor_test <- cor.test(data$price, data$sqft_living)print(cor_test)# Perform hypothesis test for the slope coefficientcoef_test <- coeftest(lm_model)print(coef_test)# One-sample mean testone_sample_test <- t.test(data$price, mu = 280000)print(one_sample_test)# Two-sample mean testtwo_sample_test <- t.test(price ~ waterfront, data = data)print(two_sample_test)# Simple linear regressionsimple_lm <- lm(price ~ sqft_living, data = data)print(summary(simple_lm))# Multiple linear regressionmlr <- lm(price ~ sqft_living + bedrooms + waterfront + condition, data = data)print(summary(mlr))# ANOVAanova_result <- anova(mlr)print(anova_result)# ANCOVAancova_model <- lm(price ~ sqft_living + bedrooms + waterfront + condition + as.factor(bedrooms), data = data)ancova_anova_result <- anova(ancova_model)print(ancova_anova_result)# One-sample test for proportionsprop_test_1 <- prop.test(sum(data$sqft_living > 0.5), length(data$sqft_living), p = 0.5)print(prop_test_1)# Two-sample test for proportionsprop_test_2 <- prop.test(table(data$waterfront), correct = FALSE)print(prop_test_2)# Logistic regression (classification)data$sold <- ifelse(data$sqft_living > 0.7, 1, 0)logit_model <- glm(sold ~ sqft_living + bedrooms + waterfront + condition, data = data, family = "binomial")print(summary(logit_model))# Scatter plot: Price vs. Square Footageggplot(data, aes(x = sqft_living, y = price)) +  geom_point() +  geom_smooth(method = "lm") +  labs(title = "Scatter Plot: Price vs. Square Footage", x = "Square Footage", y = "Price")# Box plot: Price by Conditionggplot(data, aes(x = factor(condition), y = price)) +  geom_boxplot() +  labs(title = "Box Plot: Price by Condition", x = "Condition", y = "Price")